mapper.update:
update `user`
user_name = #{username}, password = #{password}, email = #{email}, phone = #{phone}, sex = #{sex}, birthday = #{birthday}, job = #{job}, signature = #{signature}, address = #{address}
where id = #{id};

update `user`
set gmt_logout = NOW()
where id = #{userId}

mapper.select:
select id, user_name username, password, email, phone, sex, birthday, job, signature, role, gmt_create gmtCreate
from `user`
where id = #{userId};

select id, user_name username, password, email, phone, sex, birthday, job, signature, role, gmt_create gmtCreate
from `user`
where user_name = #{username};

select id, user_name username, password, email, phone, sex, birthday, job, signature, role, gmt_create gmtCreate, gmt_logout gmtLogout
from `user`
where password = #{password}

    and email = #{email}


    and phone = #{phone}
